import comments from '../model/comment';
import moment from 'moment';
import jwt from 'jsonwebtoken';


class Admin {
static  filterArticle(req, res) {
    const findArticle =   articles.filter(t => t.category === req.query.category);

    if(findArticle){
      //findArticle.sort();
     findArticle.reverse();
        return res.status(200).send({ status: 200, 'message': 'success', data:findArticle});

        }
        return res.status(404).send({ status: 404,  'message':'article not found!'});


}
static deleteComent(req, res){



      const findComment = comments.find(t => t.comment_id === parseInt(req.params.comment_id));
      if(findComment && findComment.inappropriate>=1){


        comments.splice(findComment,1);


      return res.status(200).send({ status: 200, 'message': 'comment successfully deleted', data: {comment_id:findComment.comment_id,
        comment:findComment.comment,createdOn:findComment.createdOn}});


       } 
       else {
            return res.status(404).send({ status: 404,  'message':'comment not found!'});
       }
       }
       static deleteArticle(req, res){



        const findArticle = articles.find(t => t.article_id === parseInt(req.params.article_id));
        if(findArticle && findArticle.inappropriate>=1){


          comments.splice(findArticle,1);


        return res.status(200).send({ status: 200, 'message': 'article successfully deleted', data: {title:findArticle.title,
          article:findArticle.article,createdOn:findArticle.createdOn}});


         } 
         else {
              return res.status(404).send({ status: 404,  'message':'article not found!'});
         }
         }


    }

export default Admin;

